mapper.update:
update orders
set status = #{status}, update_time = now()
where id = #{id}

mapper.select:
select

from orders
where id = #{id}
and user_id = #{userId}

select

from orders


    and user_id = #{userId}


    and order_time between '${orderTime} 00:00:00' and '${orderTime} 23:59:59'


    and status = #{status}


order by id desc
limit #{offset}, #{limit}

select count(1)
from orders


    and user_id = #{userId}


    and order_time between '${orderTime} 00:00:00' and '${orderTime} 23:59:59'


    and status = #{status}
